import React, { Component } from 'react';
import {Card,Table,Button} from 'react-bootstrap';

class searchResultUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
          apiAddress: "https://api.github.com/search/users?q="+this.props.keyword+"+in:login", 
          error: null,
          isLoaded: false,
          items: []
        };
      }
      
      updateAPI(){
        this.setState({apiAddress: "https://api.github.com/search/users?q="+this.props.keyword+"+in:login"});
      }
      componentWillReceiveProps(nextProps) {
        updateAPI();
      }
      componentDidMount() {
        fetch(this.state.apiAddress)
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                isLoaded: true,
                items: result.items
              });
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
              this.setState({
                isLoaded: true,
                error
              });
            }
          )
      }
    
      render() {
        const { error, isLoaded, items } = this.state;
        if (error) {
          return (
            <div>
              {this.state.apiAddress}
              <Card responsive >
                <Card.Body>
                  <Card.Title>Result (Error:{error.message})</Card.Title>
                </Card.Body>
              </Card>
            </div>
          );
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {
          return (
            <div>
                {this.state.apiAddress}
                <Table bordered hover responsive size="lg">
                    <thead class="thead-light text-center">
                        <tr>
                            <th>Pic</th>
                            <th>User</th>
                            <th>URL</th>
                            <th>Detail</th>
                        </tr>
                    </thead>
                    <tbody>
                    {items.map(item => (
                        <tr key={item.id} >
                            <td class="text-center">                                
                                <img src={item.avatar_url}  alt={item.login} width="50px" ></img>
                            </td>             
                            <td class="align-center">
                                {item.login} 
                            </td>                 
                            <td>
                                {item.url}
                            </td>                 
                            <td class="text-center">
                                <Button type="button" class="btn btn-primary">Detail</Button>
                            </td>   
                        </tr>
                    ))}
                    </tbody>
                </Table>             
            </div>
            
          );
        }
      }
    }
    
export default searchResultUser
