import React, { Component } from 'react';
import {Card,Table,Button} from 'react-bootstrap';

class viewUserDetail extends Component {
    constructor(props) {
        super(props);
        this.state = {
          apiAddress: "https://api.github.com/users/naichaychan", 
          error: null,
          isLoaded: false,
          items: []
        };
        this.updateApiUrl = this.updateApiUrl.bind(this);
      }
      
      componentDidUpdate() {
        var curApi=this.state.apiAddress;
        var newApi="https://api.github.com/users/naichaychan";
          if(curApi!==newApi)
          {
            this.updateApiUrl(newApi);            
            this.fetchData(newApi);
          }
          
      }

      componentDidMount() {
        this.fetchData(this.state.apiAddress);  
      }
    
      updateApiUrl(url) {
        this.setState({apiAddress:url})
      }

      fetchData(url){
        fetch(url)
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                isLoaded: true,
                items: [
                    "login": "naichaychan",
                    "id": 48716905,
                    "node_id": "MDQ6VXNlcjQ4NzE2OTA1",
                    avatar_url= "https://avatars0.githubusercontent.com/u/48716905?v=4",
                    "gravatar_id": "",
                    "url": "https://api.github.com/users/naichaychan",
                    "html_url": "https://github.com/naichaychan",
                    "followers_url": "https://api.github.com/users/naichaychan/followers",
                    "following_url": "https://api.github.com/users/naichaychan/following{/other_user}",
                    "gists_url": "https://api.github.com/users/naichaychan/gists{/gist_id}",
                    "starred_url": "https://api.github.com/users/naichaychan/starred{/owner}{/repo}",
                    "subscriptions_url": "https://api.github.com/users/naichaychan/subscriptions",
                    "organizations_url": "https://api.github.com/users/naichaychan/orgs",
                    "repos_url": "https://api.github.com/users/naichaychan/repos",
                    "events_url": "https://api.github.com/users/naichaychan/events{/privacy}",
                    "received_events_url": "https://api.github.com/users/naichaychan/received_events",
                    "type": "User",
                    "site_admin": false,
                    "name": null,
                    "company": null,
                    "blog": "",
                    "location": null,
                    "email": null,
                    "hireable": null,
                    "bio": null,
                    "public_repos": 1,
                    "public_gists": 0,
                    "followers": 0,
                    "following": 0,
                    "created_at": "2019-03-19T10:55:57Z",
                    "updated_at": "2019-03-21T05:10:16Z"
                ]
              });
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
              this.setState({
                isLoaded: true,
                error
              });
            }
          )
      }
      
      render() {
        const { error, isLoaded, items } = this.state;
        if (error) {
          return (
            <div>
              {this.state.apiAddress}
              <Card responsive >
                <Card.Body>
                  <Card.Title>Result (Error:{error.message})</Card.Title>
                </Card.Body>
              </Card>
            </div>
          );
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {
          return (
            <div>
                <Table bordered hover responsive size="lg">
                    <thead class="thead-light text-center">
                        <tr>
                            <th>Pic</th>
                            <th>User</th>
                            <th>Repository</th>
                        </tr>
                    </thead>
                    <tbody>
                    {items.map((item) => (
                        <tr key={item.id} >
                            <td class="text-center">                                
                                <img src={item.avatar_url}  alt={item.login} width="50px" ></img>
                            </td>             
                            <td class="align-center">
                                {item.login} 
                            </td>                 
                            <td>
                                {item.url}
                            </td>              
                        </tr>
                    ))}
                    </tbody>
                </Table>             
            </div>
            
          );
        }
      }
    }
    
export default viewUserDetail
