import React, { Component } from 'react';
import fetch from 'isomorphic-fetch'

class searchUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
          apiAddress: "https://api.github.com/search/users?q=A", 
          error: null,
          isLoaded: false,
          items: []
        };
      }
      componentDidMount() {
        fetch(this.state.apiAddress)
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                isLoaded: true,
                items: result.items
              });
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
              this.setState({
                isLoaded: true,
                error
              });
            }
          )
      }
    
      render() {
        const { error, isLoaded, items } = this.state;
        if (error) {
          return (
            <div>
                Error: {error.message}            
            </div>
          );
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {
          return (
            <div>
                {this.state.apiAddress}
                <table striped bordered hover>
                    <thead>
                        <tr>
                            <th>Pic</th>
                            <th>User</th>
                            <th>URL</th>
                        </tr>
                    </thead>
                    <tbody>
                    {items.map(item => (
                        <tr key={item.id}>
                            <td>                                
                                <img src={item.avatar_url}  alt="PIC" width="100px" ></img>
                            </td>             
                            <td>
                                {item.login} 
                            </td>                 
                            <td>
                                {item.url}
                            </td>                 
                            <td>
                                <input type="button" value="Detail" onClick={item.avatar_url}></input>
                            </td>   
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
            
          );
        }
      }
    }
    
export default searchUser
