import React, { Component } from 'react';
import {Table} from 'react-bootstrap';

class viewUserRepos extends Component {
    constructor(props) {
        super(props);
        this.state = {
          apiAddress: "", 
          error: null,
          isLoaded: false,
          items: []
        };
        this.updateApiUrl = this.updateApiUrl.bind(this);
      }
      
      componentDidUpdate() {
        var curApi=this.state.apiAddress;
        var newApi="https://api.github.com/users/"+this.props.gitUser+"/repos";
        
        alert("Start : "+curApi+" New : "+newApi);           
          if(curApi!==newApi)
          {
            this.updateApiUrl(newApi);            
            // this.fetchData(newApi);
            
          }
          this.fetchData(newApi);
          
          alert("dfg");
          
      }

      componentDidMount() {
          
        var curApi=this.state.apiAddress;
        var newApi="https://api.github.com/users/"+this.props.gitUser+"/repos";
          if(curApi!==newApi)
          {
            this.updateApiUrl(newApi);            
            alert("Start : "+curApi+" New : "+this.state.apiAddress);           
          }
        // this.fetchData(this.state.apiAddress);  
      }
    
      updateApiUrl(url) {
        this.setState({apiAddress:url})
      }

      fetchData(url){
        fetch(url)
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                isLoaded: true,
                items: result
              });
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
              this.setState({
                isLoaded: true,
                error
              });
            }
          )
      }

      render() {
        const { error, isLoaded, items } = this.state;
        if (error) {
          return (
            <div>
            {this.props.gitUser}
              Error:{error.message}
            </div>
          );
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {
          return (
            <div>
                {this.props.gitUser}
                <Table bordered hover responsive size="lg">
                    <thead class="thead-light text-center">
                        <tr>
                            <th>No.</th>
                            <th>Repository Name</th>
                            <th>Detail</th>
                        </tr>
                    </thead>
                    <tbody>
                    {items.map(( item, index ) => (
                        <tr key={index} >
                            <td class="text-center">
                                {index+1} 
                            </td>  
                            <td >
                                {item.name} 
                            </td>                 
                            <td>
                                {item.description}
                            </td>         
                        </tr>
                    ))}
                    </tbody>
                </Table>             
            </div>
            
          );
        }
      }
    }
    
export default viewUserRepos
