import React, { Component } from 'react';
import {Table,Button} from 'react-bootstrap';
import ViewUserDetail from './viewUserDetail';

import ReactDOM from 'react-dom';

class searchResultUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
          apiAddress: "https://api.github.com/search/users?q="+this.props.keyword+"+in:login+type:user", 
          error: null,
          isLoaded: false,
          items: []
        };
        this.updateApiUrl = this.updateApiUrl.bind(this);
      }
      
      componentDidUpdate() {
        var curApi=this.state.apiAddress;
        var newApi="https://api.github.com/search/users?q="+this.props.keyword+"+in:login+type:user";
          if(curApi!==newApi)
          {
            this.updateApiUrl(newApi);            
            this.fetchData(newApi);
          }
          
      }

      componentDidMount() {
        this.fetchData(this.state.apiAddress);  
      }
    
      updateApiUrl(url) {
        this.setState({apiAddress:url})
      }

      fetchData(url){
        fetch(url)
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                isLoaded: true,
                items: result.items
              });
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
              this.setState({
                isLoaded: true,
                error
              });
            }
          )
      }
      btn_detail_onClick(userName){
        if(!this.state.isLoaded)
        {
          window.location.replace("/viewUserDetail/"+userName);
        }
        
      }

      render() {
        const { error, isLoaded, items } = this.state;
        if (error) {
          return (
            <div>
              Error:{error.message}
            </div>
          );
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {
          return (
            <div>
                <Table bordered hover responsive size="lg">
                    <thead class="thead-light text-center">
                        <tr>
                            <th>Pic</th>
                            <th>User</th>
                            <th>URL</th>
                            <th>Detail</th>
                        </tr>
                    </thead>
                    <tbody>
                    {items.map(item => (
                        <tr key={item.id} >
                            <td class="text-center">                                
                                <img src={item.avatar_url}  alt={item.login} width="50px" ></img>
                            </td>             
                            <td>
                                {item.login} 
                            </td>                 
                            <td>
                                {item.url}
                            </td>                 
                            <td class="text-center">
                                <button class="btn btn-primary" onClick={this.btn_detail_onClick(item.login)}>Detail</button>
                            </td>   
                        </tr>
                    ))}
                    </tbody>
                </Table>             
                {this.setState({isLoaded:false})}
            </div>
            
          );
        }
      }
    }
    
export default searchResultUser
