import React, { Component } from 'react';
import {Card,Button} from 'react-bootstrap';
import searchResultUser from './searchResultUser';


class searchUser extends Component {  
  constructor(props) {
      super(props);
      this.state = {searchKeyword: "A"};
  
      // This binding is necessary to make `this` work in the callback
      this.handleClick = this.handleClick.bind(this);
    }


    handleClick() {
      this.setState(state => ({
        searchKeyword: "B"
      }));
    }
  

    render() {
      
      if(this.state.searchKeyword==="")
      {
        this.setState({searchKeyword:"A"})
      }
      return(
        <div>
            <div class="form-row mt-3">
              <div class="form-group col-12">
                <Card>
                    <Card.Body>
                      <Card.Title>Search user on git hub</Card.Title>
                        <div class="input-group mb-3">
                          <div class="input-group-prepend">
                            <span class="input-group-text" id="basic-addon1">User : </span>
                          </div>
                          <input type="text" class="form-control mr-3" placeholder={this.state.searchKeyword} aria-label="Username" aria-describedby="basic-addon1" id="SearchInput"/>
                          {/* <Button type="button" class="btn btn-primary" onClick={btn_search_onClick}> Search</Button> */}
                          
      <button onClick={this.handleClick}>
        {this.state.searchKeyword}
      </button>
                        </div>  
                    </Card.Body>
                </Card>  
              </div>
            </div>
            <div class="form-row">
              <div class="form-group col-12">
                <Card>
                  <Card.Body>
                    <Card.Title>Result ({this.state.searchKeyword})</Card.Title>
                      <div class="input-group mb-3">
                        <searchResultUser id="resultTable" keyword={this.state.searchKeyword}/>
                      </div>  
                    </Card.Body>
                </Card>  
              </div>
            </div>            
        </div>
      );
    }
  }
    
export default searchUser
