import React, { Component } from 'react';
import {Card,Table,Button,ButtonToolbar} from 'react-bootstrap';

class viewUserDetail extends Component {
    constructor(props) {
        super(props);
        this.state = {
          apiAddress: "https://api.github.com/users/naichaychan", 
          error: null,
          isLoaded: false,
          itemsInfo: [],
          itemsDetail: []
        };
        this.updateApiUrl = this.updateApiUrl.bind(this);
      }
      
      componentDidUpdate() {
        var curApi=this.state.apiAddress;
        var newApi="https://api.github.com/users/naichaychan";
          if(curApi!==newApi)
          {
            this.updateApiUrl(newApi);            
            this.fetchDataInfo(newApi);
          }
          
      }

      componentDidMount() {
        this.fetchDataInfo(this.state.apiAddress);  
      }
    
      updateApiUrl(url) {
        this.setState({apiAddress:url})
      }

      fetchDataInfo(url){
        fetch(url)
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                isLoaded: true,
                items: result
              });
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
              this.setState({
                isLoaded: true,
                error
              });
            }
          )
      }
      
      render() {
        const { error, isLoaded, items } = this.state;
        if (error) {
          return (
            <div>
              {this.state.apiAddress}
              <Card responsive >
                <Card.Body>
                  <Card.Title>Result (Error:{error.message})</Card.Title>
                </Card.Body>
              </Card>
            </div>
          );
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {
          return (
            <div>
                <div class="card-body">
                <div class="form-row">
                        <div class="form-group col-12">
                            <Table bordered hover responsive size="lg">
                                <thead class="thead-light text-center">
                                    <tr>
                                        <th>Pic</th>
                                        <th>User</th>
                                        <th>Total Repository</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    <tr>
                                        <td class="text-center">                                
                                            <img src={items.avatar_url}  alt={items.login} width="50px" ></img>
                                        </td>             
                                        <td>
                                            {items.login} 
                                        </td>                 
                                        <td class="text-center">
                                            {items.public_repos+items.public_gists}
                                        </td>              
                                    </tr>
                                </tbody>
                            </Table>     
                        </div>
                    </div>
                    <div class="form-row">
                        <div class="form-group col-12">
                            <Table bordered hover responsive size="lg">
                                <thead class="thead-light text-center">
                                    <tr>
                                        <th>No.</th>
                                        <th>Repository Name</th>
                                        <th>Description</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    <tr>
                                        <td class="text-center">                                
                                            <img src={items.avatar_url}  alt={items.login} width="50px" ></img>
                                        </td>             
                                        <td>
                                            {items.login} 
                                        </td>                 
                                        <td class="text-center">
                                            {items.public_repos+items.public_gists}
                                        </td>              
                                    </tr>
                                </tbody>
                            </Table>     
                        </div>
                    </div>
                </div>            
            </div>            
          );
        }
      }
    }
    
export default viewUserDetail
