import React, { Component } from 'react';
import fetch from 'isomorphic-fetch'

import { Table,Card } from 'react-bootstrap';

class searchUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
          apiAddress: "https://api.github.com/search/users?q=A", 
          error: null,
          isLoaded: false,
          items: []
        };
      }
      componentDidMount() {
        fetch(this.state.apiAddress)
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                isLoaded: true,
                items: result.items
              });
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
              this.setState({
                isLoaded: true,
                error
              });
            }
          )
      }
    
      render() {
        const { error, isLoaded, items } = this.state;
        if (error) {
          return (
            <div>
                Error: {error.message}            
            </div>
          );
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {
          return (
            <div>
                {this.state.apiAddress}
                <Card style={{ width: '18rem' }}>
  <Card.Img variant="top" src="holder.js/100px180" />
  <Card.Body>
    <Card.Title>Card Title</Card.Title>
    <Card.Text>
      Some quick example text to build on the card title and make up the bulk of
      the card's content.
    </Card.Text>
    <Button variant="primary">Go somewhere</Button>
  </Card.Body>
</Card>
                <Table striped bordered hover responsive>
                    <thead class="thead-light text-center">
                        <tr>
                            <th>Pic</th>
                            <th>User</th>
                            <th>URL</th>
                            <th>Detail</th>
                        </tr>
                    </thead>
                    <tbody>
                    {items.map(item => (
                        <tr key={item.id}>
                            <td class="text-center">                                
                                <img src={item.avatar_url}  alt="PIC" width="100px" ></img>
                            </td>             
                            <td class="align-center">
                                {item.login} 
                            </td>                 
                            <td>
                                {item.url}
                            </td>                 
                            <td class="text-center">
                                <input type="button" value="Detail" onClick={item.avatar_url}></input>
                            </td>   
                        </tr>
                    ))}
                    </tbody>
                </Table>
            </div>
            
          );
        }
      }
    }
    
export default searchUser
