import React, { Component } from 'react';
import {Card,Table,Button,ButtonToolbar} from 'react-bootstrap';

class viewUserDetail extends Component {
    constructor(props) {
        super(props);
        this.state = {
          apiAddress: "https://api.github.com/users/naichaychan", 
          error: null,
          isLoaded: false,
          items: []
        };
        this.updateApiUrl = this.updateApiUrl.bind(this);
      }
      
      componentDidUpdate() {
        var curApi=this.state.apiAddress;
        var newApi="https://api.github.com/users/naichaychan";
          if(curApi!==newApi)
          {
            this.updateApiUrl(newApi);            
            this.fetchData(newApi);
          }
          
      }

      componentDidMount() {
        this.fetchData(this.state.apiAddress);  
      }
    
      updateApiUrl(url) {
        this.setState({apiAddress:url})
      }

      fetchData(url){
        fetch(url)
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                isLoaded: true,
                items: result
              });
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
              this.setState({
                isLoaded: true,
                error
              });
            }
          )
      }
      
      render() {
        const { error, isLoaded, items } = this.state;
        if (error) {
          return (
            <div>
              {this.state.apiAddress}
              <Card responsive >
                <Card.Body>
                  <Card.Title>Result (Error:{error.message})</Card.Title>
                </Card.Body>
              </Card>
            </div>
          );
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {
          return (
            <div>
                <Table bordered hover responsive size="lg">
                    <thead class="thead-light text-center">
                        <tr>
                            <th>Pic</th>
                            <th>User</th>
                            <th>Total Repository</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td class="text-center">                                
                                <img src={items.avatar_url}  alt={items.login} width="50px" ></img>
                            </td>             
                            <td class="align-center">
                                {items.login} 
                            </td>                 
                            <td>
                                {items.public_repos+items.public_gists}
                            </td>              
                        </tr>
                    </tbody>
                    <tfoot>
                        <tr>
                            <td colSpan="3" class="text-center">
                                <ButtonToolbar>
                                    <Button variant="primary">See more..</Button>
                                </ButtonToolbar>
                            </td>
                        </tr>
                    </tfoot>
                </Table>     
            </div>
            
          );
        }
      }
    }
    
export default viewUserDetail
